import { useState, useEffect , useCallback} from "react";


import React from "react";
import {
  Form,
  Button,
  Label,
  FormGroup,
  CustomInput,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";
import "assets/css/nucleo-icons.css";
import "assets/scss/blk-design-system-react.scss";
import "assets/demo/demo.css";
// reactstrap components

export default function Etud() {

  const [message, setMessage] = useState("");
  const [answer, setAnswer] = useState("");

  const handleSpeechRecognition = useCallback(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = "fr-FR";

    recognition.onstart = () => {
      console.log("Speech recognition started");
    };

    recognition.onresult = (event) => {
      let interimTranscript = "";
      let finalTranscript = "";

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;

        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      setMessage(finalTranscript);
    };

    recognition.onerror = (event) => {
      console.error(event.error);
    };

    recognition.onend = () => {
      console.log("Speech recognition ended");
      recognition.start();
    };

    recognition.start();

    return () => {
      recognition.stop();
    };
  }, []);

  const handleSubmit = useCallback(async () => {
    if (message) {
      // Send the message to your Flask backend
      const response = await fetch("http://localhost:5000/answeretud", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ message }),
      });

      const data = await response.json();
      console.log(data);
      setAnswer(data.answer);
    }
  }, [message]);

  useEffect(() => {
    handleSpeechRecognition();
  }, [handleSpeechRecognition]);

  useEffect(() => {
    const timeoutId = setTimeout(handleSubmit, 4000);
    return () => clearTimeout(timeoutId);
  }, [handleSubmit]);

  useEffect(() => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.stop();

    return () => {
      const newRecognition = new window.webkitSpeechRecognition();
      newRecognition.stop();
    };
  }, []);
  return (
    <div className="page-header header-filter">
    <div className="squares square1" />
    <div className="squares square2" />
    <div className="squares square3" />
    <div className="squares square4" />
    <div className="squares square5" />
    <div className="squares square6" />
    <div className="squares square7" />
    <Container>
      <div className="content-center brand">
    <h1 style={{marginTop:'380px'}}>ESPACE PROF</h1>
      <h2>Sp√©cifier precisemment votre choix:</h2>
      <div>
      <Form /*  onSubmit={handleSubmit} */>
        <Input
          type="text"
          value={message}
          onChange={(event) => setMessage(event.target.value)}
        />


      <Input
          type="text"
          value={answer}
        />  
        </Form>

    </div>

             </div>
                </Container>
              </div>
            );
          }
