import React, { useState,useR } from 'react';
import axios from 'axios';

function Profile() {
  const [name, setName] = useState('');
  const [info, setInfo] = useState('');
  const [image, setImage] = useState(null);
  const [email, setEmail] = useState('');
  const [profs, setProfs] = useState([]);

  useEffect(() => {
    // Get all entries from the Prof table
    axios.get('/get-profs')
      .then(response => {
        setProfs(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  const handleNameChange = event => {
    setName(event.target.value);
  }

  const handleInfoChange = event => {
    setInfo(event.target.value);
  }

  const handleImageChange = event => {
    setImage(event.target.files[0]);
  }

  const handleEmailChange = event => {
    setEmail(event.target.value);
  }

  const handleSubmit = event => {
    event.preventDefault();

    // Upload the image to the server
    const formData = new FormData();
    formData.append('image', image);
    axios.post('/upload-image', formData)
      .then(response => {
        console.log(response.data.message);
        // Add a new entry to the Prof table
        axios.post('/add-prof', { name, info, image: response.data.filename, email })
          .then(response => {
            console.log(response.data.message);
            // Update the list of entries from the Prof table
            axios.get('/get-profs')
              .then(response => {
                setProfs(response.data);
              })
              .catch(error => {
                console.log(error);
              });
          })
          .catch(error => {
            console.log(error);
          });
      })
      .catch(error => {
        console.log(error);
      });

    // Clear the form inputs
    setName('');
    setInfo('');
    setImage(null);
    setEmail('');
  }

  return (
    <div className="App">
      <h1>Add a new Prof</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input type="text" value={name} onChange={handleNameChange} />
        </label>
        <br />
        <label>
          Info:
          <textarea value={info} onChange={handleInfoChange} />
        </label>
        <br />
        <label>
          Image:
          <input type="file" onChange={handleImageChange} />
        </label>
        <br />
        <label>
          Email:
          <input type="text" value={email} onChange={handleEmailChange} />
        </label>
        <br />
        <button type="submit">Add Prof</button>
      </form>

      <h1>Profs</h1>
      <ul>
        {profs.map((prof, index) => (
          <li key={index}>
            <strong>{prof.Name}</strong> ({prof.Email})<br />
            {prof.Info}<br />
            <img src={`http://localhost:5000/images/${prof.Image}`} alt={prof.Name} />
          </li>
        ))}
      </ul>
    </div>
  );
}

export default  Profile
  ;
