import { Routes , Route , useNavigate } from "react-router-dom";
import React from "react";
import { useState , useEffect, useRef  } from "react";

import axios from 'axios';

import {
  Button,
  Label,
  CustomInput,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Row,
  Col
} from "reactstrap";

// reactstrap components

export default function Face() {
    const [videoUrl, setVideoUrl] = useState(null);
    const [names, setNames] = useState([0, 0]);

    useEffect(() => {
      // Fetch the video stream URL from the Flask endpoint
      axios.get('http://localhost:5000//video').then(response => {
        setVideoUrl(URL.createObjectURL(response.data));
      });

      // Fetch the face recognition results from the Flask API every 1 second
      setInterval(() => {
        axios.get('http://localhost:5000/api/face_recognition').then(response => {
          setNames(response.data.names);
        });
      }, 1000);
    }, []);

    return (
      <div>
        <h1>Face Recognition App</h1>
        <p>Number of unknown faces: {names[0]}</p>
        <p>Number of known faces: {names[1]}</p>
        {videoUrl ? (
          <video src={videoUrl} autoPlay />
        ) : (
          <p>Loading video stream...</p>
        )}
      </div>
    );
};
