import classnames from "classnames";
// plugin that creates slider
import Slider from "nouislider";

// reactstrap components


import { Routes , Route , useNavigate } from "react-router-dom";
import React from "react";
import {
  Button,
  Label,
  FormGroup,
  CustomInput,
  Input,
  InputGroupAddon,
  InputGroupText,
  InputGroup,
  Container,
  Col
} from "reactstrap";
import { useState ,useEffect } from "react";import "assets/css/nucleo-icons.css";
import "assets/scss/blk-design-system-react.scss";
import "assets/demo/demo.css";
// reactstrap components

export default function PageHeader() {
  useEffect=() =>{
    redirect()
      }


      const redirect=async(e)=>{
        const res=await fetch('http://localhost:5000/open_website');
        const data = await res.json();


      };
const navigate = useNavigate();
const navigatetoprofil=()=>{
navigate('/result')
}
const navigatetosujet=()=>{
navigate('/sujet')
}
const navigatetoresult=()=>{
navigate('/result')
}

  const [inputFocus, setInputFocus] = React.useState(false);
  const slider1 = React.useRef(null);
  const slider2 = React.useRef(null);
  React.useEffect(() => {
    Slider.create(slider1.current, {
      start: [40],
      connect: [true, false],
      step: 1,
      range: { min: 0, max: 100 }
    });
    Slider.create(slider2.current, {
      start: [20, 60],
      connect: [false, true, false],
      step: 1,
      range: { min: 0, max: 100 }
    });
  }, []);

  return (
    <div className="page-header header-filter">
      <div className="squares square1" />
      <div className="squares square2" />
      <div className="squares square3" />
      <div className="squares square4" />
      <div className="squares square5" />
      <div className="squares square6" />
      <div className="squares square7" />
      <Container>
        <div className="content-center brand">
          <h1 className="h1-seo">BLKâ€¢ React</h1>
          <h3 className="d-none d-sm-block">
            A beautiful Design System for Bootstrap 4 (reactstrap) and React.
            It's Free and Open Source.
          </h3>
          <Button className="btn-round" color="primary" type="button" onClick={(e)=>{navigatetoprofil(e)}}>
            Espace profils
            </Button>
            <Button className="btn-round" color="primary" type="button" onClick={(e)=>{navigatetosujet(e)}}>
Espace sujet
                         </Button>
                         <Button className="btn-round" color="primary" type="button" onClick={(e)=>{navigatetoresult(e)}}>
Espace resultat                         </Button>
<Button className="btn-round" color="primary" type="button" /* onClick={(e)=>{navigatetoprofil(e)}} */>
            Espace events
            </Button>
            <Button className="btn-round" color="primary" type="button" /* onClick={(e)=>{navigatetosujet(e)}} */>
Espace emploi
                         </Button>
                         <Button className="btn-round" color="primary" type="button" /* onClick={(e)=>{navigatetoresult(e)}} */>
Espace resultat                         </Button>
        </div>
      </Container>
    </div>
  );
}
