         navigatetoprof(true);
       }, 5000);
      }


  }) */
if(message==="identifier moi")
  axios
  .get("http://127.0.0.1:5000/login")
  .then((res) => {
    console.log(res);
    setName(res.data) ;
    setimagePath(res.statusText); })
    .then((res)=>{
      setlogged_in(true)
      console.log(logged_in)

      if (res!== (null || "You are unknown")){
        setTimeout((i) => {
         navigatetoprof(true);
       }, 5000);
