import { useState, useEffect } from "react";
import { Col } from "reactstrap";


export default function Général() {
  const [message, setMessage] = useState("");

  const handleSpeechRecognition = () => {
    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = "fr-FR";

    recognition.onstart = () => {
      console.log("Speech recognition started");
    };

    recognition.onresult = (event) => {
      let interimTranscript = "";
      let finalTranscript = "";

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;

        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      setMessage(finalTranscript);
    };

    recognition.onerror = (event) => {
      console.error(event.error);
    };

    recognition.onend = () => {
      console.log("Speech recognition ended");
      handleSpeechRecognition();
    };

    recognition.start();
  };

  useEffect(() => {
    handleSpeechRecognition();
    handle
  }, []);
  const handleSubmit = async (event) => {
    event.preventDefault()

    // Send the message to your Flask backend
    const response = await fetch("http://localhost:5000/answerprof", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ message }),
    });

    const data = await response.json();
    console.log(data);
    setAnswer(data.answer)

  }
  return (
    <div>
      <h1 style={{ marginTop: "80px" }}>ESPACE Professeur</h1>
      <h2>Spécifier precisemment votre choix:</h2>
      <div>
        <form>
          <textarea
            value={message}
            onChange={(event) => setMessage(event.target.value)}
          />
        </form>
      </div>
    </div>
  );
}
