import { Routes , Route , useNavigate } from "react-router-dom";
import axios from "axios";
import Webcam from 'react-webcam';
import React, { useState, useEffect, useRef } from "react";

export default function Face ()  {
  const handleSpeechRecognition =() => {

    const recognition = new window.webkitSpeechRecognition();
    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = "fr-FR";

    recognition.onstart = () => {
      console.log("Speech recognition started");
    };

    recognition.onresult = (event) => {
      let interimTranscript = "";
      let finalTranscript = "";

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;

        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      setMessage(finalTranscript);
    };

    recognition.onerror = (event) => {
      console.error(event.error);
    };

    recognition.onend = () => {
      console.log("Speech recognition ended");
      handleSpeechRecognition();
    };

    recognition.start();
  }
  const webcamRef = useRef(null);
    const [name, setName] = useState(null);

    const [logged_in, setlogged_in] = useState(false);
    const navigate = useNavigate();


  const capture = async () => {

    const imageSrc = webcamRef.current.getScreenshot();
    if (!imageSrc) {
      console.log("dfgf")
      return;
    }
    const formData = new FormData();
    formData.append('image', imageSrc);
if(mes)
      fetch('http://localhost:5000/login', {
        method: 'POST',

        body:
          formData        
      })
        .then((response) =>{ 
          console.log(response)
          setName(response.statusText)
        setlogged_in(true)
      })


      .catch(error => {
        console.error(error);
      });

    /*   const response = await axios.post('http://localhost:5000/login', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }); */

        /* setName(response.data);
      } catch (error) {
        console.log(error);
        setName("Error: face not recognized");
      } */

  }
  return (
    <div className="page-header header-filter">
    <div className="wrapper">
  <div className="page-header">
  <div className="content-center">


      <div className="content-center brand" ></div>

    <>
        {logged_in === false ?
        <>
          <Webcam
        audio={false}
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        width={640}
        height={480}
      />
        <div className="form-group"  >
            <input type="submit" value="Authenticate Me" onClick={()=>{capture()}} className="btn btn-primary" />
        </div>

        </>
        :
        <div>

           {name !== (null || "You are unknown") ?
        <>
            <div className="details">
                <h1>Hello {name} !</h1>
                <br />


                <div className="form-group">
{/*                       <input type="submit" value="Logout" onClick={()=>{this.setState({msg:null,logged_in:false})}} className="btn btn-primary" />
*/}                  </div>  
            </div>
            </>
        :
        <div>
          <p>"unknown"</p>
        </div>
        }
        </div>

        }  </>


  </div>
  </div>
  </div>
  </div>


)
}
