import { useRoutes } from "react-router-dom";
import { lazy } from "react";

import { Navigate } from "react-router-dom";
import Login from "../auth/Login.js";
import { useAuthContext } from "../shared/useAuthContext";
import { Table } from "reactstrap";
const FullLayout = lazy(() => import("../layouts/FullLayout.js"));

const Home= lazy(() => import("../pages/Home"));
const Tables= lazy(() => import("../pages/Tables"));
const Prof= lazy(() => import("../pages/profiles/prof"));
const Student= lazy(() => import("../pages/student"));






export default function Router(){
  const { user } = useAuthContext() 

  let ThemeRoutes = useRoutes([

    {  path: "/login",
    element:!user ? <Login/> : <Navigate to="/" />}


      ,

      { path: "/",
      element: <FullLayout /> ,
      children: [
        { path: "/", element: <Home/> },
        {path: "/table",
        element:  <Tables /> /* : <Navigate to="/login" /> */},
        {path: "/prof",exact:true,
        element:  <Prof />/*  : <Navigate to="/login" />, */},
        {path: "/student",exact:true,
        element:  <Student />/*  : <Navigate to="/login" />, */},

      ]
      },
    ]);


return ThemeRoutes ;}
