import '../dash.scss'
import axios from 'axios';
import { Row, Col, Table, Card, CardTitle, CardBody, Badge,Form,FormGroup,InputGroup,InputGroupText ,Label} from "reactstrap";
import { AiOutlineSearch } from 'react-icons/ai';
import studentsService from "../../services/student.service" ;
import Swal from 'sweetalert2';
import { useState ,useEffect } from "react";
import { Input } from "reactstrap";
import { Button, Modal, ModalHeader, ModalBody } from 'reactstrap';

import { useAuthContext } from '../../shared/useAuthContext'; 
import { useDataContext } from '../../hooks/useDataContext';
import profService from '../../services/prof.service';

const Prof = () => {

  const [searchtext,setsearchtext]=useState("");
  const [modal, setModal] = useState(false);
  const [id, setId] = useState(null);
  const [submitted, setSubmitted] = useState(null);

  const { user } = useAuthContext()
 const { Data ,dispatch } = useDataContext()
   const[ Json,setjson]=useState('')


  const initialTutorialState = {
    id: null,
    Nom: "",
    Info: "",
    Contact: "",
    Email:"",

    Image: ""


  };
  const [currentUser, setCurrentUser] = useState(initialTutorialState);

const handleInputChange = event => {
  const { name, value } = event.target;
  setCurrentUser({ ...currentUser, [name]: value });
};
  const Submit = event => {
    event.preventDefault();

    // Upload the image to the server
    const formData = new FormData();
    formData.append('image', currentUser.Image);
    axios.post('http://localhost:5000/upload-image-prof', formData)
      .then(response => {
        console.log(response.data.message);
        // Add a new entry to the Prof table
        const prof ={Image:currentUser.Image,Nom:currentUser.Nom,Info:currentUser.Info,Contact:currentUser.Contact,Email:currentUser.Email}

        axios.post('http://localhost:5000/addprof', prof)
          .then(response => {
            console.log(response.data.message);
            // Update the list of entries from the Prof table
            axios.get('http://localhost:5000/getprof')
              .then(response => {
                console.log(response)
                setMessage("The tutorial was updated successfully!");
                setSubmitted(true);
                setState(!state)
              })
              .catch(error => {
                console.log(error);
              });
          })
          .catch(error => {
            console.log(error);
          });
      })
      .catch(error => {
        console.log(error);
      });}

  const [state,setState]=useState(false)
  const[message,setMessage] =useState('');

  const toggle = () => setModal(!modal);
  const[result,setResult]=useState([])
 useEffect(() => {
    const fetchData = async () => {

      profService.getProf().then(async res=>{
        if(res){


            dispatch({type: 'SET_Data', payload: res.data})
            setjson(res.data)
        }

      })
      .catch({})
    }


      fetchData()

  }, [dispatch,state])
  const handleSubmit = async(e) => {
    e.preventDefault();
     const prof ={Image:currentUser.Image,Nom:currentUser.Nom,Info:currentUser.Info,Contact:currentUser.Contact,Email:currentUser.Email}
        console.log(prof)
        profService.save(prof).then(res=>{ 
          if (res){
            console.log(res)

        setMessage("The tutorial was updated successfully!");
        setSubmitted(true);
        setState(!state)
      }
    else 
  {
/*     setError("an error occured")
 */  }
 })

  }



const editt=(data,e)=>{
   e.preventDefault();
  toggle() ;
  setId(data.id);
  setCurrentUser(data);

  console.log(data.id);

  console.log(modal)
}


const update=async(e)=>{
  e.preventDefault();
  toggle() ;
  e.preventDefault();
  profService.updateProf(id,currentUser).then(res=>{ 
  if (res){
  Swal.fire({ 

  text:   "the pre-selectioned member was updated successfully",
  confirmButtonText: 'Cancel',
  icon : "success" ,
  confirmButtonColor:"#979292 "

}) 
setMessage("The tutorial was updated successfully!");
setState(!state)
}})
   .catch(e => {
     console.log(e);

   })



} 



 const Delete=(id,e)=>{
  console.log(id)
  Swal.fire({
           text:   "Are you sure you want to delete this User ?",
           icon: 'warning' ,
           showCancelButton: true,  
   confirmButtonText: `Yes`,  
   confirmButtonColor: "#ff6873", 

         }).then((result) => {
           if (result.isConfirmed) {

 deletee(id,e) ;   
 Swal.fire({text:   "User was deleted successfully",
 icon: 'success' ,
 confirmButtonText: `OK`, 
 confirmButtonColor: "#ff6873",
 })         
           } else
           Swal.fire({text:   "Cancelled",
           icon: 'error' ,
           confirmButtonText: `OK`, 
           confirmButtonColor: "#ff6873",
     })

       }) 


 } 
const deletee =async(tdata,e)=>{

  e.preventDefault();
  profService.deleteProf(tdata.id).then(res=>{
    if(res){
      dispatch({type: 'DELETE_Data', payload: tdata})
      setState(!state)
    }

  }).catch(e => {
    console.log(e);
  })

} 






  return (
    <div>


    <Row>

      <Col >
      <Form className="navbar-search navbar-search-dark " onSubmit={(e)=>{Submit(e)}}>
            <FormGroup className="mb-0">
              <div style={{borderRadius:30  ,marginTop:'10px',marginBottom:'10px',width:'300px',float:"right"}}>
              <InputGroup className="input-group-alternative">
                <InputGroupText style={{backgroundColor:"transparent", borderColor:"#7a7878" }}>

                  <AiOutlineSearch style={{color:"#555555"}} />
                </InputGroupText>
                <Input  placeholder="Search" type="text" style={{backgroundColor:"transparent", borderColor:" #7a7878"}}  onChange={(event)=>{setsearchtext(event.target.value)}} />
              </InputGroup>
              </div>
            </FormGroup>

        <Card style ={{width:1500 ,marginLeft:'-40px'}} >
          <CardTitle tag="h6" className="border-bottom p-3 mb-0">
            <i className="bi bi-card-text me-2"> </i>
            Members
          </CardTitle>
          <CardBody style ={{width:1500}}>
          <Row onSubmit={handleSubmit}>
      <Col style={{marginLeft:'30px'}}>
        <Label>Image:</Label>
        <Input
         type="file" 
          name="Image"
          id="Image"
          value={currentUser.Image}
          onChange={(e) => handleInputChange(e)} 
        />
      </Col>
      <Col>
        <Label>Name:</Label>
        <Input
          type="text"
          name="Nom"
          id="Nom"

          value={currentUser.Nom}
          onChange={(e) => handleInputChange(e)} style={{width:'250px'}}
        />
      </Col>
      <Col>
        <Label>Info:</Label>
        <Input
          type="text"
          name="Info"
          id="Info"

          value={currentUser.Info}
          onChange={(e) => handleInputChange(e)} style={{width:'300px'}}
        />
      </Col>
      <Col>
        <Label>Contact:</Label>
        <Input
          type="text"
          name="Contact"
          id="Contact"

          value={currentUser.Contact}
          onChange={(e) => handleInputChange(e)} style={{width:'250px'}}
        />
      </Col>
      <Col >
        <Label>email:</Label>
        <Input
          type="text"
          name="Email"
          id="Email"

          value={currentUser.Email}
          onChange={(e) => handleInputChange(e)} style={{width:'250px'}}
        />
      </Col>
       <Col>
       <Button type="submit" style={{borderRadius:30  ,marginTop:'35px',marginBottom:'10px',width:'70px',float:'flex-end'}}>OK</Button>

      </Col>

    </Row>
    </CardBody>
    </Card>
    </Form>

    <Card style ={{width:1500 ,marginLeft:'-40px'}}>
    <CardBody style ={{width:1550,marginLeft:'-40px'}}>

            <Table className="no-wrap  align-left" responsive borderless >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Image</th>

                  <th>Name</th>
                  <th>Info</th>
                  <th>Contact</th>
                  <th>Email</th>



                  { <th>Actions</th>}  

                </tr>
              </thead>


              <tbody>
             {Json && Json.filter((val)=>{
                if(searchtext==="")
                return val ;
                else if (val.name.toLowerCase().includes(searchtext.toLowerCase())){
                  return val
                }
              }).map((tdata, index) => ( 
                    <tr key={index}>
                  <td>{index+1}</td> 
                    { <td>{tdata.Image &&<> <img src={`http://localhost:5000/images/${tdata.Image}`} alt="image"  className="avatar rounded-circle mr-3" style={{width:"40px",height:"40px"}} /> </>}   </td> }


                <td>{tdata && tdata.Nom}</td>
            <td style={{width:400}}>{tdata && tdata.Info}</td>

            <td>{tdata && tdata.Contact}</td>
            <td >{tdata && tdata.Email}</td>



                      <td style={{width:"250px",float:"right"}}> <><Button className ="edit" id="b5" style={{marginTop:-2 ,float:"right",alignItems: 'flex-end'}}  >
                  <i className="bi bi-pencil-fill" style ={{fontSize: 20+"px",color:"yellow"}}  onClick={(e)=>editt(tdata,e) } ></i>


                </Button> 

                <Button id ="b4"   onClick={(e)=>Delete(tdata,e)}      > 
                      <span className="trash">
                      <span></span>
                        <i></i>

                      </span>
                    </Button> </> 



                          </td> 


                      </tr> ))} 
                      </tbody> 



            </Table>

          </CardBody>
        </Card>
      </Col>
    </Row>

    <Modal isOpen={modal} toggle={toggle} {...result} className="Modal1"  size="lg" style={{maxWidth: '2000px', width: '100%'}}>
    <ModalHeader toggle={toggle}>Modal title</ModalHeader>
    <ModalBody>
    <form
          id="form"
          action="/"
          method="POST"

        >
    <Table borderless>
    <thead>
                <tr>
                  <th className ="th">Name</th>

                   <th>Image</th>
                  <th>info</th>
                  <th>contact</th>
                  <th>Email</th>







                </tr>
              </thead>


              <tbody>


          <tr>
            <td>

              <div className='inline'>
                <Row>
                  <Col>

                  <Input 
              placeholder=" nom"
              type="string"
              name="Nom" id="Nom"
              className="input-field"
              onChange={(e) => handleInputChange(e)}
              value={currentUser.Nom}
            />   </Col>  


                </Row>

          </div>
          <td>
          <Input 
   placeholder="Image"
   type="file"
   name="Image"
   id="Image"
   className="input-field"
   onChange={(e) => handleInputChange(e)}
   value={currentUser.Image}
   required
 > 
                </Input> </td>
            </td>
            <td>

            <Input  
              placeholder="info"
              type="string"
              name="Info"
              id="Info"
              className="input-field"
              onChange={(e) => handleInputChange(e)}
              value={currentUser.Info}
            />



          </td>
            <td>

 <Input 
   placeholder="contact"
   type="string"
   name="Contact"
   id="Contact"
   className="input-field"
   onChange={(e) => handleInputChange(e)}
   value={currentUser.Contact}
   required
 > 
                </Input>  </td>
                <td>

 <Input 
   placeholder="Email"
   type="string"
   name="Email"
   id="Email"
   className="input-field"
   onChange={(e) => handleInputChange(e)}
   value={currentUser.Email}
   required
 > 
                </Input>  </td>






          </tr>


                  </tbody>
                  </Table>  
                  <Button type="submit" id="submit-button" onClick={e=>update(e)} >
            submit
          </Button>  
        </form>
    </ModalBody>

  </Modal>

  </div>
  );
};

export default Prof;
