import { Button, Nav, NavItem,Collapse,NavLink, Dropdown } from "reactstrap";
import { Link, useLocation } from "react-router-dom";
import { useState ,useEffect } from "react";

const navigation = [
  {
    title:"Schedules : temps /ds/exam/rattrapage",
    icon: "bi bi-speedometer2",
    children:[
      {
        title:"Temps ",
        href:"/temps"
      },
      {
        title:"ds ",
        href:"/ds"
      },
      {
        title:"exam ",
        href:"/exam"
      },
      {
        title:"rattrapage ",
        href:"/rattrapage"
      }
    ]
  },

  {
    title: "profiles:prof/étudiant",
    icon: "bi bi-layout-split",
    children:[
      {
        title:"prof ",
        href:"/prof"
      },
      {
        title:"students ",
        href:"/student"
      },]
  },

  {
    title: "events",
    href: "/administration",
    icon: "bi bi-laptop",
  },
  {
    title: "Meet",
    icon: "bi bi-textarea-resize",
    children: [
      {
        title: "Online",
        href: "/online",
      },
      {
        title: "Présentiel",
        href: "/presentiel",
      },
    ],
  },
  {
    title: "Result",
    icon: "bi bi-list",
    children: [
      {
        title: "Exam",
        href: "/exam",
      },
      {
        title: "PFE",
        href: "/pfe",
      },
      {
        title: "PCD",
        href: "/pcd",
      },
      {
        title: "PE",
        href: "/result/pe",
      },
    ],
  },
  {
    title: "Subjects",
    icon: "bi bi-envelope-open",
    children: [
      {
        title: "PCD",
        href: "/pcd",
      },
      {
        title: "PFE",
        href: "/pfe",
      },
      {
        title: "PE",
        href: "/pe",
      },
    ],
  },


];

const Sidebar = () => {
  const showMobilemenu = () => {
    document.getElementById("sidebarArea").classList.toggle("showSidebar");



  };
  let location = useLocation();
  const [isOpen, setIsOpen] = useState(false);
    const toggle = () => setIsOpen(!isOpen);
  return (
     <div className="p-3">
      <div className="d-flex align-items-center">

      <Button
          close
          size="sm"
          className="ms-auto d-lg-none"
          onClick={() => showMobilemenu()}
        ></Button>
      </div>
      <div className="pt-4 mt-2">

        <Nav vertical className="sidebarNav">
          {navigation.map((item, index) => (
            <NavItem key={index} className="sidenav-bg">
         {item.children ?(  

         <NavLink
                href="#" onClick={toggle}
                className={
                  location.pathname === item.href
                    ? "text-primary nav-link py-3"
                    : "nav-link text-secondary py-3"
                }
              >
                 <i className={item.icon}></i>
                <span className="ms-3 d-inline-block">{item.title}</span> <i className="bi-chevron-down toggle-btn" onClick={() => setIsOpen(!isOpen)}></i>


              </NavLink> ):(   <NavLink
                href={item.href}
                className={
                  location.pathname === item.href
                    ? "text-primary nav-Navlink py-3"
                    : "nav-link text-secondary py-3"
                }
              >
                <i className={item.icon}></i>
                <span className="ms-3 d-inline-block">{item.title}</span> <i className="bi-chevron-down toggle-btn" onClick={() => setIsOpen(!isOpen)}></i>


              </NavLink>)}

          {item.children && (
          <Collapse isOpen={isOpen} style={{marginLeft:"70px"}} >
            {item.children.map((subitem,subindex)=>( <NavItem key={subindex}>
                  <NavLink href={subitem.href}>{subitem.title}</NavLink>
                </NavItem>
          ))}

        </Collapse>)}

            </NavItem>
          ))}
        </Nav>
      </div>
    </div> 

  );

};

export default Sidebar;
